INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (0,0,'NAO SE APLICA','Sem Conceito');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (1,1,'CONSULTA AMBULATORIAL','Atendimento dispensado a indivíduos cuja condição de saúde estável lhes permita comparecer ao estabelecimento e retornar ao local de origem, realizado por profissionais de saúde de nível superior, com a finalidade de fornecer parecer, instrução ou examinar determinada situação, afim de decidir sobre um plano de ação ou prescrição terapêutica dentro da sua área de atuação.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (2,1,'APOIO DIAGNOSTICO','Ações e serviços que se utilizam de recursos físicos e tecnológicos (ex: Raios X, Ultrassonografia, Ressonância Magnética, Análises Clínicas/ Laboratoriais, Eletrocardiografia, Endoscopia,etc) com o objetivo de auxiliar, de forma complementar, a determinação da natureza de uma doença ou estado, ou a diferenciação entre elas, melhorando a tomada de decisão assistencial.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (3,1,'TERAPIAS ESPECIAIS','Atividades voltadas exclusivamente para a realização de hemodiálise, quimioterapia, radioterapia ou cirurgias ambulatoriais.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (4,1,'REABILITACAO','Conjunto de ações e serviços orientados a desenvolver ou ampliar a capacidade funcional e desempenho dos indivíduos, proteger a saúde e prevenir agravos, de modo a contribuir para autonomia, acesso à direitos e participação em todas as esferas da vida social.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (5,1,'CONCESSAO, MANUTENCAO E ADAPTACAO DE OPM','As Órteses, Próteses, Materiais Especiais e Meios de Locomoção (OPM) constituem ferramentas do processo terapêutico da reabilitação, contribuindo fundamentalmente na superação de barreiras, devendo ser prescritas de forma individualizada por profissional capacitado. A concessão de OPM deve obrigatoriamente estar atrelada à adaptação, manutenção e treino de uso da mesma.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (6,1,'ATENCAO DOMICILIAR','Ações e serviços prestados de forma substitutiva ou complementar à internação hospitalar ou atendimento ambulatorial, caracterizados pelo conjunto de tratamento de doenças, reabilitação, promoção à saúde e prevenção, englobando internação e/ou assistência prestadas em domicílio.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (7,1,'ASSISTENCIA A EMERGENCIAS','Cuidados destinados a pacientes de demanda espontânea com agravos que necessitam de atendimento imediato por risco iminente de morte.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (8,1,'ENTREGA/DISPENSACAO DE MEDICAMENTOS','Conjunto de ações relativas ao fornecimento de medicamentos diretamente ao paciente e a orientação para o seu uso racional, mediante apresentação de prescrição por profissional habilitado.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (9,1,'INTERNACAO','Cuidados ou tratamentos prestados a um indivíduo, por razões clínicas e/ou cirúrgicas, que demandem a ocupação de um leito por um período igual ou superior a 24 horas.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (10,1,'ASSISTENCIA INTERMEDIARIA','Conjunto de ações realizadas entre a internação e o atendimento ambulatorial, para realização de procedimentos clínicos, cirúrgicos, diagnósticos e terapêuticos, que requeiram a permanência do paciente em um leito por um período inferior a 24 horas.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (11,1,'ATENCAO PSICOSSOCIAL','Conjunto de ações intersetoriais de caráter territorial e comunitário que visa à substituição do modelo asilar manicomial, por meio de cuidados que possibilitem a reabilitação psicossocial das pessoas em sofrimento psíquico ou transtorno mental, incluindo aquelas com necessidades decorrentes do uso de álcool e outras drogas, garantindo atenção contínua às situações de crise em saúde mental e articulação do cuidado com outros pontos de atenção.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (12,1,'ATENCAO BASICA','Conjunto de ações e serviços longitudinais de saúde no âmbito individual e coletivo, de caráter territorial e comunitário, que abrange o cuidado/tratamento, a promoção e proteção da saúde, a prevenção de agravos, a vigilância em saúde, a reabilitação e a redução de danos à saúde, coordenando ou integrando o cuidado fornecido em outros pontos de atenção.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (13,1,'ASSISTENCIA OBSTETRICA E NEONATAL','Conjunto de cuidados ou tratamentos prestados à gestante, parturiente e recém-nascido, por razões obstétricas ou neonatais.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (14,1,'TELESSAUDE','Serviços que utilizam tecnologias da informação e comunicação como meio para desenvolver ações de apoio a Atenção à Saúde e de Educação Permanente em Saúde, com o fim de realizar apoio diagnóstico, ações educativas, esclarecer dúvidas dos profissionais de saúde e gestores de saúde.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (15,1,'ATENCAO HEMATOLOGICA E/OU HEMOTERAPICA','Conjunto de ações que integram a assistência especializada em coagulopatias e hemoglobinopatias e/ou o conjunto de ações referentes a captação do doador, o ciclo de produção do sangue, testes sorológicos, testes imunohematológicos, distribuição e transfusão de sangue e componentes e demais atividades hemoterápicas.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (16,1,'PROMOCAO DA SAUDE, PREVENCAO DE DOENCAS E AGRAVOS E PRODUCAO DO CUIDADO','Conjunto de ações e serviços de saúde, de caráter individual ou coletivo, compreendendo práticas corporais, artísticas e culturais, práticas integrativas e complementares, atividades físicas, promoção da alimentação saudável ou educação em saúde.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (17,1,'IMUNIZACAO','Conjunto de ações que objetivam a administração de vacinas para estimulação da resposta imune do hospedeiro, incluindo quaisquer preparações para a profilaxia imunológica ativa.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (18,2,'ANALISES LABORATORIAIS DE VIGILANCIA EM SAUDE','Ações e serviços de análises laboratoriais de interesse à saúde pública, relacionadas a vigilância epidemiológica, vigilância em saúde ambiental, saúde do trabalhador e vigilância sanitária, vinculado a órgãos ou entidades da Administração Pública direta ou indireta, da União, dos Estados, do Distrito Federal ou dos Municípios, e das fundações mantidas pelo poder Público.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (19,2,'VIGILANCIA EM SAUDE DO TRABALHADOR','Ações e serviços de vigilância em saúde, clínico-assistenciais e de suporte técnico-pedagógico, voltadas para a atenção integral à saúde do trabalhador.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (20,2,'ESCLARECIMENTO DA CAUSA MORTIS DE OBITOS','Ações e serviços de realização de autópsia para o esclarecimento dacausa mortis, com ou sem assistência médica, sem elucidação diagnóstica, em especial aqueles sob investigação epidemiológica.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (21,2,'VIGILANCIA DE ZOONOSES','Conjunto de ações, serviços e estratégias relacionadas a animais de relevância para a saúde pública, devendo obrigatoriamente contemplar: a educação em saúde visando à prevenção de zoonoses; a vacinação animal contra zoonoses normatizadas pelo Ministério da Saúde; o diagnóstico laboratorial de zoonoses e identificação das espécies de animais; a coleta, recebimento, acondicionamento, conservação e transporte de espécimes ou amostras biológicas de animais para encaminhamento aos laboratórios, com vistas à identificação ou diagnóstico laboratorial de zoonoses; o gerenciamento de resíduos oriundos dos serviços gerados pelas atividades de vigilância em zoonoses; o recolhimento e transporte de animais; a recepção de animais vivos e de cadáveres de animais; a manutenção e cuidados básicos aos animais recolhidos; a destinação adequada dos animais recolhidos (resgate, transferência ou eutanásia); a necropsia e coleta de amostras laboratoriais ou outros procedimentos pertinentes em animais (vivos ou mortos) suspeitos de zoonoses; o controle de vetores, roedores e animais sinantrópicos; a adoção de medidas de biossegurança; e a inspeção zoossanitária. Não estão incluídas: atividades que possuem como foco o bem estar, a proteção e a saúde animal; fiscalização relativa a maus tratos a animais; salvamento, recolhimento e acolhimento de animais que não são de relevância para a saúde pública; controle e prevenção de acidentes de trânsito causados por animais; atendimento de reclamações relativas às denúncias que não se referem a animais de relevância para a saúde pública; adestramento/ressocialização de animais; controle em massa de populações de animais para prevenção de zoonoses; atendimento clínico veterinário a animais que não estejam sob guarda da Unidade de Vigilância de Zoonoses (UVZ); atendimento clínico veterinário a animais que estejam sob guarda da UVZ, que não seja exame clínico básico e procedimentos curativos; atendimento cirúrgico veterinário a animais.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (22,3,'ADMINISTRACAO','Compreende os processos de formulação, implementação, planejamento e administração das políticas, sistemas e práticas de saúde. Abrange a organização dos serviços de saúde, bem como a articulação das práticas de saúde (individual e coletiva) nos níveis central, regional e local de um sistema ou rede de saúde. Inclui-se as atividades realizadas para a gestão, controle, avaliação e auditoria de sistemas ou redes de saúde, bem como a atividade administrativa do Tratamento Fora de Domicílio. Exclui-se as atividades de gestão interna nos estabelecimentos e serviços de saúde.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (23,3,'REGULACAO ASSISTENCIAL','Compreende a organização, o controle, o gerenciamento e a priorização do acesso e dos fluxos assistenciais, por meio de autoridade sanitária exercida por profissional de saúde no seu âmbito de atuação legal, para a garantia do acesso baseada em protocolos, classificação de risco e demais critérios de priorização. Inclui-se as atividades de regulação assistencial realizadas tanto pelo Sistema Único de Saúde quanto pelas operadoras de planos de saúde, seja hospitalar ou ambulatorial, eletiva ou de urgência, as atividades de regulação de ambulâncias públicas ou privadas.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (24,3,'LOGISTICA DE INSUMOS','Compreende o armazenamento e distribuição, sem fins comerciais, para os estabelecimentos de saúde, de medicamentos, imunobiológicos, kit de diagnóstico, produtos químicos e equipamentos de controle vetorial ou produtos para a saúde.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (25,3,'LOGISTICA DE ORGAOS, TECIDOS E CELULAS DO CORPO HUMANO','Compreende as ações para o acondicionamento, gerenciamento e distribuição dos órgãos, tecidos e células do corpo humano.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (26,4,'HOSPITALIDADE','Serviços que visam alojar temporariamente e apoiar indivíduos que necessitam permanecer fora de sua residência/moradia para acessar serviços de saúde não ofertados em sua localidade de origem, podendo dispor de atividades assistenciais simples, principalmente relacionados a cuidados básicos.');
INSERT INTO atividade(id,grupoatividade_id,nome,conceito) VALUES (27,4,'CONFECCAO DE ORTESES E PROTESES DENTARIAS','Serviço de fabricação e produção de órteses e próteses dentárias com o intuito de apoiar ações de assistência em saúde.');
